import Foundation

// Defines a list of recipe categories.
// Conforms to CaseIterable to allow iteration over all cases,
// and Identifiable to uniquely identify each category by its raw value.

enum Categories: String, CaseIterable, Identifiable {
    var id: String { self.rawValue } // Provides a unique identifier for each category (required for ForEach in SwiftUI)
    case main = "Main"
    case breakfast = "Breakfast"
    case lunch = "Lunch"
    case dinner = "Dinner"
    case dessert = "Dessert"
}

// Represents a recipe with relevant information.
// Conforms to Identifiable so it can be used easily in SwiftUI views like List or ForEach.

struct Recipe: Identifiable{
    let id = UUID()
    let name: String
    let image: String
    let descriptions: String
    let ingredients: String
    let directions: String
    let category: Categories.RawValue
    let datePublished: String
    let url: String
}

